import React from 'react'
import Progressbar from "./progressbar"
import { Progress } from 'semantic-ui-react'
class CyberExposureScore extends React.Component{
    constructor(props){
        super()
        this.state={
            count:0
        }
    }
cyberexposurecount(){
    
}
render(){

    return(
        <div className="ces">
            <div className="ces1">
                <p> Cyber Exposure Score</p>
            </div>
            <div className="ces2">
                <p><small>
                Companies are given exposure score based on identified risk
                divided by employee count on last 12 months. Score ranges from 0
                to 300+.Companies with no exposure during the past 12 months have score 0.
                Companies that have score 300+ represent the top 10% most exposed Companies.
                </small>
                    
                </p>
            </div>
            <div className="ces3">
                <div className="ces31">
                    <h1> <bold> 169</bold></h1>
                </div>
                <div className="ces32">
                    <p>/300</p>
                    
                </div>
                

            </div>

            <div className="ces4">
                
                <Progressbar/>
            </div>
        
        <style jsx>{`
        .ces{
            display:flex;
            flex-direction:column;
            
            margin-left:5%;
            height:100%;
            width:90%;
            justify-content:space-evenly;
        }
        .ces1{
            display:flex;
            align-items:flex-start;
            justify-content:center;
            
            font-size:20px;
            height:10%;
            over-flow:auto;
            
        }
        .ces2{
            
            display:flex;
            align-items:center;
            justify-content:center;
            position:relative;
            height:20%;
            over-flow:auto;
            
        }
        .ces3{
            display:flex;
            flex-direction:row;
            
            height:20%;
            justify-content:space-evenly;
            
        }
        .ces31{
            display:flex;
            height:90%;
            width:49%;
            align-items:center;
            justify-content:flex-end;
            font-size:31px;
            padding-top:30px;
        }
        .ces32{
            display:flex;
            width:49%;
            height:100%;
            align-items:center;
            justify-content:flex-start;
            font-size:25px;
            padding-top:34px;
        }
        .ces4{
            
            height:20%;
        }
        
        `}


        </style>
        
        </div>
    )

}

}
export default CyberExposureScore;
