import React,{Component} from 'react';
class Alertsdropdown extends React.Component{
	constructor(props){
		super(props)
        this.state={
            showa:"false"
        }
	}
  backdropClickHandler =() => {
    this.setState((prevState) => {
            return {showa: !prevState.showa};

        
    });


  };

  render(){
  return(
            <div className="dropdown-content"      style={{display:this.state.show?"none":"flex",transition:"background,color 0.5s ease-in"}}
              onMouseLeave={this.backdropClickHandler} >
            <li className="spacer1"></li>
            <a href="#" className="dashboards">
            All Alerts
            </a>
            <a href="#">
            Sensitive Information</a>
            <a href="#">
            Discussions</a>
            <a href="#">
            Black Markets</a>
            <a href="#">
            Financial</a>
            <a href="#">
            Financial</a>
            <a href="#">
            Financial</a>

          
        


        </div>
  )

  }
}

export default Alertsdropdown;
