import React,{Component} from 'react';
import Link from 'next/link';
import Alertsdropdown from "./alertsdropdown.js"
class Navigation extends React.Component{
	constructor(props){
		super(props)
        this.state={
            show:false,
            showa:false
        }
        this.backdropClickHandlert=this.backdropClickHandlert.bind(this)
        this.backdropClickHandlerf=this.backdropClickHandlerf.bind(this)
        this.backdropClickHandler1t=this.backdropClickHandler1t.bind(this)
        this.backdropClickHandler1f=this.backdropClickHandler1f.bind(this)
	}
  backdropClickHandler =() => {
    this.setState((prevState) => {
            return {show: !prevState.show};

        
    });


  };

    backdropClickHandler1 =() => {
    this.setState((prevState) => {
            return {showa: !prevState.showa};        
    });


  };
  backdropClickHandlert(){
    this.setState({show:true})

  };
    backdropClickHandlerf(){
    this.setState({show:false})
  };
  backdropClickHandler1t(){
    this.setState({showa:true})

  };
    backdropClickHandler1f(){
    this.setState({showa:false})
  };
render(){
		return(
    <div>
        <nav className="designing">
            <li className="logo"> CYBER INTELLIGENCE HOUSE  </li>
            <li className="link1"   onMouseEnter={this.backdropClickHandlert}
               onMouseLeave={this.backdropClickHandlerf}
   > Exposed Information </li>

            <li className="link2"> Assets </li>
            <li className="link3"> Support </li>
            <li className="spacer"></li>
            <Link href="/" ><a><img src="usericon.png" className="userlogo"/></a></Link>
            <li className="demoaccount"> Demo account </li>
            <li className="logo1"> logo </li>
          </nav>
          <div className="dropdown-content"      style={{display:this.state.show?"flex":"none"}}
          onMouseEnter={this.backdropClickHandlert}       >
          <li className="spacer1"></li>
            <a href="#" className="dashboards">
            <img src="favicon.ico"/><p>DASHBOARDS</p>
            </a>
            <a href="#">
<img src="favicon.ico"/>
            KEYWORDS</a>
            <a href="#" onMouseEnter={this.backdropClickHandler1t}
               onMouseLeave={this.backdropClickHandler1f}
                 
             >
<img src="favicon.ico"/>
            ALERTS
            </a>
            <a href="#">
<img src="favicon.ico"/>
            REMEDIATION</a>
            <a href="#">
<img src="favicon.ico"/>
            SETTINGS</a>

          
        </div>

            <div className="dropdown-content"  style={{display:this.state.showa?"flex":"none"}}    
                 >
            <li className="spacer1"></li>
            <a href="#" className="dashboards">
            <img src="favicon.ico"/>
            All Alerts
            </a>
            <a href="#">
            Sensitive Information</a>
            <a href="#">
            Discussions</a>
            <a href="#">
            Black Markets</a>
            <a href="#">
            Financial</a>
            <a href="#">
            Exposed Credentials</a>
            <a href="#">
            Personal Information</a>
            <a href="#">
            Hacker Group Targeting</a>
            <a href="#">
            Attack & Compromises</a>
            <a href="#">
            Under Analysis</a>

            </div>
          

          <style jsx>{`

.designing{
  background: black;
  display: flex;
  flex-direction: row;
  align-items: center;
  width:100%;
  
  

}
.designing >li {
  color:white;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 50px;
  
  
  list-style:none; 
}
.logo{
  display: flex;

  flex-basis: 40%;
}
.link1{
    display: flex;
    font-family: 'Gilroy', sans-serif;
  width:150px;
  font-size: 14px;
}
.link2{
    display: flex;
    width:70px;
    font-family: "Gilroy", sans-serif;
    font-size: 14px;
}
.link3{
    display: flex;
    width:70px;
       font-family: "Gilroy", sans-serif;
   font-size: 14px;
}
.spacer{
  display: flex;
  flex-basis: 20%;

  
}
.userlogo{
  display: flex;
flex-basis: 15%;
    justify-content: flex-end;
background: black;
  height: 25px;
  width: 25px;
}
.demoaccount{
    display: flex;
    justify-content: flex-end;
       font-family: "Gilroy", sans-serif;
   font-size: 14px;
   width: 110px;

}
.logo1{

}
/* Dropdown Button */


/* The container <div> - needed to position the dropdown content */

.spacer1{
  display: flex;
  flex-basis: 20%;
}
/* Dropdown Content (Hidden by Default) */
.dropdown-content {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
align-items: center;
  background: white;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  z-index: 1;
}

/* Links inside the dropdown */
.dropdown-content >a {
  color: black;
  text-decoration: none;
    display:flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
      font-family: 'Gilroy', sans-serif;
  font-size: 14px;
  width: 170px;
  height:70px;
}

/* Change color of dropdown links on hover */
.dropdown-content a:hover {background-color: #ddd;}

/* Show the dropdown menu on hover */


/* Change the background color of the dropdown button when the dropdown content is shown */
.link1:hover {background-color: #3e8e41;}


.dashboards{
  display: flex;
  flex-direction: row;
  align-items: center;
width: 100px;

}

          `} </style>
    
    </div>
			)
	}
}
export default Navigation;
